;div class=\"col-xs-12 col-sm-8\"&gt;&lt;input id=\"c-type\" class=\"form-control\" name=\"row[\'+max+\'][type]\" type=\"text\" value=\"\"&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=\"form-group\"&gt;&lt;label class=\"control-label col-xs-12 col-sm-2\"&gt;面积&lt;/label&gt;&lt;div class=\"col-xs-12 col-sm-8\"&gt;&lt;input id=\"c-size\" class=\"form-control\" name=\"row[\'+max+\'][size]\" type=\"text\" value=\"\"&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=\"form-group\"&gt;&lt;label class=\"control-label col-xs-12 col-sm-2\"&gt;价格&lt;/label&gt;&lt;div class=\"col-xs-12 col-sm-8\"&gt;&lt;input id=\"c-price\" class=\"form-control\" name=\"row[\'+max+\'][price]\" type=\"text\" value=\"\"&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;\';\r\n                    $(\'.data\').after(str);\r\n\r\n                   \r\n                }\r\n\r\n                \r\n                \r\n            });\r\n\r\n\r\n        \r\n         \r\n\r\n            // 移除户型\r\n            $(document).on(\'click\', \'.del_he\', function(){\r\n               \r\n                var key = $(this).attr(\'data-key\');\r\n                console.log(key);\r\n\r\n                var id = $(\'#id\'+key).val();\r\n                if(id!=null &amp;&amp; id&gt;0){\r\n                    // alert(\'该户型有id\'+id);\r\n                    var url = \'/h.php/typeimages/del\';\r\n                    \r\n                    $.post(url, { action:\'del\',ids:id,params:\'\'},\r\n                    function(res){\r\n                        console.log(res);\r\n                        if(res.code==1){\r\n                            $(\'.view\'+key).remove();\r\n                        }else{\r\n                            alert(res.msg);\r\n                        }\r\n                        \r\n                    });\r\n\r\n                }else{\r\n                    $(\'.view\'+key).remove();\r\n                }\r\n            });\r\n\r\n            $(document).on(\"click\", \".add_he\", function(){\r\n        \r\n                Controller.api.bindevent();\r\n            });\r\n            \r\n\r\n        },\r\n\r\n\r\n        api: {\r\n            bindevent: function () {\r\n                Form.api.bindevent($(\"form[role=form]\"));\r\n            }\r\n        }\r\n    };\r\n    return Controller;\r\n});<br></code></pre>', 1, 1629449549, 1629449549, 1629449549, NULL, '', '', 'TpMeCMS');
