INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (113, NULL, 'Fastadmin 后台表格每行数据加按钮弹新页面教程及更具条件显示或隐藏', NULL, '', '', NULL, '', 113, 414, '', '<p>在对应的js里面编辑</p><p><br></p><p>找到如下代码：</p><pre><code class=\"lang-js\">    {field: \'operate\', title: __(\'Operate\'), table: table,<br></code></pre><p>并在后面添加：可自行修改</p><pre><code class=\"lang-js\">                        {field: \'operate\', title: __(\'Operate\'), table: table,\r\n                            buttons: [\r\n                                {\r\n                                    name: \'editprice\',\r\n                                    text: \'编辑附近大学\',\r\n                                    title: \'编辑附近大学\',\r\n                                    classname: \'btn btn-xs btn-success btn-dialog\',\r\n                                    icon: \'fa fa-folder-o\',\r\n                                    url: \'shop/yuyue_order2/editprice\'\r\n                                }\r\n                            ], \r\n                            events: Table.api.events.operate, formatter: Table.api.formatter.operate}<br></code></pre><p><br></p><p><br></p><p><br></p><p>如果要更具条件来显示或隐藏该按钮在里面加入</p><pre><code class=\"lang-js\">   &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hidden:function(row){\r\n                                            if(row.xiaohe==0){//xiaohe就是这个数据的字段 自行修改\r\n                                                return false; //显示该按钮\r\n                                            }else{\r\n                                                return true;&nbsp;&nbsp;//隐藏该按钮\r\n                                            }\r\n                                        },<br></code></pre><p>示例用法</p><pre><code>       {field: \'operate\', title: __(\'Operate\'), table: table,\r\n                            buttons: [\r\n                                {\r\n                                    name: \'editprice\',\r\n                                    text: \'编辑附近大学\',\r\n                                    title: \'编辑附近大学\',\r\n                                    classname: \'btn btn-xs btn-success btn-dialog\',\r\n                                    icon: \'fa fa-folder-o\',\r\n\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//隐藏按钮条件\r\n   &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;hidden:function(row){\r\n                                            if(row.end_status==0){  // 自行修改条件\r\n                                                return false; //显示该按钮\r\n                                            }else{\r\n                                                return true;  //隐藏该按钮\r\n                                            }\r\n                                    },\r\n\r\n                                    url: \'shop/yuyue_order2/editprice\'\r\n                                }\r\n                            ], \r\n                            events: Table.api.events.operate, formatter: Table.api.formatter.operate}<br></code></pre>', 1, 1626143944, 1627636259, 1626143865, NULL, '', '', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (114, NULL, '宝塔定时任务执行每30秒执行一次', NULL, '', '', NULL, '', 114, 372, '', '<p>更改前</p><pre><code class=\"lang-bash\">#!/bin/bash\r\nPATH=/bin:/sbin:/usr/bin:/usr/sbin:/usr/local/bin:/usr/local/sbin:~/bin\r\nexport PATH\r\ncurl -sS --connect-timeout 10 -m 3600 \'http://newk3.he4966.cn/api/twhttp/update_data\'\r\necho \"----------------------------------------------------------------------------\"\r\nendDate=`date +\"%Y-%m-%d %H:%M:%S\"`\r\necho \"★[$endDate] Successful\"\r\necho \"----------------------------------------------------------------------------\"\r\n<br></code></pre><p><br></p><p><br></p><p><br></p><p>更改后</p><pre><code class=\"lang-bash\">#!/bin/bash\r\nPATH=/bin:/sbin:/usr/bin:/usr/sbin:/usr/local/bin:/usr/local/sbin:~/bin\r\nexport PATH\r\nstep=30 //时间\r\nfor (( i = 0; i &lt; 60; i=(i+step) )); do\r\ncurl -sS --connect-timeout 10 -m 3600 \'http://newk3.he4966.cn/api/twhttp/update_data\'\r\necho \"----------------------------------------------------------------------------\"\r\nendDate=`date +\"%Y-%m-%d %H:%M:%S\"`\r\necho \"★[$endDate] Successful\"\r\necho \"----------------------------------------------------------------------------\"\r\nsleep $step\r\ndone\r\nexit 0\r\n<br></code></pre>', 1, 1626857801, 1626857801, 1626857661, NULL, '', '', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (115, 2, 'thinkphp5 的Db查询用法', NULL, '', '', NULL, '', 115, 532, '', '<p><span style=\"color:#7CA668;\">随机取值 不会重复，数量可能没有10个</span></p>\r\n<pre><code class=\"lang-php\">$list&nbsp;=&nbsp;Db::name(\'addr\')-&gt;where(\'rand()\')-&gt;limit(10)-&gt;column(\'id\');\r\n</code></pre>\r\n<p><span style=\"color:#7CA668;\">V5.0.17+版本开始，当你的order排序中使用了SQL函数的时候，请使用orderRaw方法替代order，例如：</span></p>\r\n<pre><code class=\"lang-php\">$list&nbsp;=&nbsp;Db::name(\'addr\')-&gt;orderRaw(\'rand()\')-&gt;limit(10)-&gt;column(\'id\');//随机取值 不会重复 建议用此方法</code></pre>\r\n<p><br /></p>\r\n<pre class=\"language-php\"><code>//查询不同的值\r\nDb::table(\'user\')-&gt;distinct(true)-&gt;field(\'school\')-&gt;select();</code></pre>\r\n<p><br /></p>\r\n<pre class=\"language-php\"><p><code>//按照IDS的顺序来排序结果</code></p><p><code>        $exp = (\'field(id,\'.$ids.\')\');</code></p><code>        return Db::name(\'size\')\r\n        -&gt;where(\'id\',\'in\',$ids)\r\n        -&gt;orderRaw($exp)\r\n        -&gt;select();</code></pre>\r\n<br /><p><br /></p><p><br /></p><p>模糊搜索2个或多个字段，只要字段里面包含其中数据即可搜索出来</p>\r\n<pre><code class=\"lang-php\">        $where[\'core|ltd_name\'] = [\'like\',\'%\'.$key.\'%\'];\r\n       \r\n        $list = Db::name(\'ltd\')\r\n                -&gt;where($where)\r\n                -&gt;where(\'siteswitch\',\'1\')//可以关联其他字段\r\n                -&gt;where(\'uid\',\'&gt;\',0)//或者判断大于多少\r\n                -&gt;select();\r\n</code></pre>\r\n<p><br /></p><p><br /></p><p><span style=\"color:#525252;font-size:15px;\"><b>使用getTableInfo可以获取表信息，信息类型 包括 fields,type,bind,pk，以数组的形式展示，可以指定某个信息进行获取</b></span></p>\r\n<pre><code>// 获取`think_user`表所有信息\r\nDb::getTableInfo(\'think_user\');\r\n// 获取`think_user`表所有字段\r\nDb::getTableInfo(\'think_user\', \'fields\');\r\n// 获取`think_user`表所有字段的类型\r\nDb::getTableInfo(\'think_user\', \'type\');\r\n// 获取`think_user`表的主键\r\nDb::getTableInfo(\'think_user\', \'pk\');\r\n</code></pre>\r\n<p>查询表达式支持大部分的SQL查询语法，也是<code>ThinkPHP</code>查询语言的精髓，查询表达式的使用格式：</p>\r\n<pre><code><span class=\"token function\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#DD4A68;\">where</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">(</span><span class=\"token string\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#669900;\">\'字段名\'</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">,</span><span class=\"token string\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#669900;\">\'表达式\'</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">,</span><span class=\"token string\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#669900;\">\'查询条件\'</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">)</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">;</span> <span class=\"token function\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#DD4A68;\">whereOr</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">(</span><span class=\"token string\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#669900;\">\'字段名\'</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">,</span><span class=\"token string\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#669900;\">\'表达式\'</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">,</span><span class=\"token string\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#669900;\">\'查询条件\'</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">)</span><span class=\"token punctuation\" style=\"box-sizing:inherit;-webkit-tap-highlight-color:transparent;text-size-adjust:none;-webkit-font-smoothing:antialiased;color:#999999;\">;</span> </code></pre>\r\n<p>表达式不分大小写，支持的查询表达式有下面几种，分别表示的含义是：</p><p><table><colgroup><col width=\"49.89384288747346%\" /><col width=\"50.10615711252654%\" /></colgroup><thead><tr><th>表达式</th><th>含义</th></tr></thead><tbody><tr><td>EQ、=</td><td>等于（=）</td></tr><tr><td>NEQ、&lt;&gt;</td><td>不等于（&lt;&gt;）</td></tr><tr><td>GT、&gt;</td><td>大于（&gt;）</td></tr><tr><td>EGT、&gt;=</td><td>大于等于（&gt;=）</td></tr><tr><td>LT、&lt;</td><td>小于（&lt;）</td></tr><tr><td>ELT、&lt;=</td><td>小于等于（&lt;=）</td></tr><tr><td>LIKE</td><td>模糊查询</td></tr><tr><td>[NOT] BETWEEN</td><td>（不在）区间查询</td></tr><tr><td>[NOT] IN</td><td>（不在）IN 查询</td></tr><tr><td>[NOT] NULL</td><td>查询字段是否（不）是NULL</td></tr><tr><td>[NOT] EXISTS</td><td>EXISTS查询</td></tr><tr><td>EXP</td><td>表达式查询，支持SQL语法</td></tr><tr><td>&gt; time</td><td>时间比较</td></tr><tr><td>&lt; time</td><td>时间比较</td></tr><tr><td>between time</td><td>时间比较</td></tr><tr><td>notbetween time</td><td>时间比较<br /><br /></td></tr></tbody></table><span style=\"font-size:small;\"><br /></span></p><p><span style=\"font-size:small;\"> </span></p>\r\n<pre class=\"language-php\"><p><code>                       //闭包写法</code></p><p><code>&nbsp;&nbsp;&nbsp;&nbsp;                   Db::name(\'goodsorder\')</code></p><code>                        -&gt;where($where)\r\n                        -&gt;where(\'status\',\'2\')\r\n                        -&gt;where(function ($wait)   {\r\n                            $wait-&gt;whereOr(\"send_status\",\'2\');//发货状态:1=待支付,2=待收货,3=已收货,4=已完成,5=已退款\r\n\r\n                            $wait-&gt;whereOr(\"delivery_status\",\'1\');//自提送货状态:1=配送中,2=待自提,3=已自提\r\n                            $wait-&gt;whereOr(\"delivery_status\",\'2\');//自提送货状态:1=配送中,2=待自提,3=已自提\r\n                        })\r\n                        // -&gt;where(\'send_status|delivery_status\',\'2\')//如果2个字段查询都是同一个值也可以这样写\r\n                        // -&gt;whereOr(\'delivery_status\',\'2\')//自提送货状态:1=配送中,2=待自提,3=已自提\r\n</code><p><code>                        -&gt;count();</code></p><p><code>//\r\n</code></p>\r\n\r\n\r\n\r\n\r\n\r\n\r\n<pre id=\"response26\" style=\"box-sizing:border-box;overflow:auto;font-family:Menlo, Monaco, Consolas, &quot;Courier New&quot;, monospace;padding:9.5px;margin-top:0px;margin-bottom:10px;line-height:1.42857;color:#333333;word-break:break-all;overflow-wrap:break-word;background-color:#F5F5F5;border:1px solid #CCCCCC;border-radius:4px;\"><code><span class=\"string\" style=\"box-sizing:border-box;color:green;\">SELECT COUNT(*) AS tp_count FROM `fa_goodsorder` WHERE  `uid` = 1001  AND `status` = \'2\'  AND (  `send_status` = \'2\'  OR ( `delivery_status` = \'1\' OR `delivery_status` = \'2\' ) ) LIMIT 1\"</span></code></pre>\r\n<code><div style=\"color:#C5C8C6;background-color:#1E1E1E;font-family:Menlo, Monaco, &quot;Courier New&quot;, monospace;font-size:12px;line-height:18px;white-space:pre;\"><br /></div>\r\n<pre class=\"language-php\"><code>       //闭包 传值查询\r\n            -&gt;where(function($w) use($tagcar_arr) {\r\n                foreach ($tagcar_arr as $key =&gt; $tagcar_id) {\r\n                    $whereor[] = [\'exp\',Db::raw(\"FIND_IN_SET(\'$tagcar_id\',tagcar_ids)\")];\r\n                    $w-&gt;whereOr($whereor);\r\n                }\r\n            })\r\n            -&gt;page($page,$limit)\r\n</code><p><code>            // -&gt;fetchSql(true)//返回查询的sql语句放中间 true 不执行</code></p></pre>\r\n<br /> </code><p><br /></p></pre>\r\n<p><code><br /></code></p><p><code><span style=\"font-size:21px;\">时间查询</span></code></p>\r\n<p><br /></p>\r\n<pre class=\"language-php\"><code>// 大于某个时间\r\nwhere(\'createtime\', \'&gt; time\', \'2020-5-20\');\r\n// 小于某个时间\r\nwhere(\'createtime\', \'&lt;= time\', \'2022-7-21\');\r\n// 时间区间查询\r\nwhere(\'createtime\', \'between time\', [\'2020-5-20\', \'2022-7-21\']);\r\n\r\n// 时间区间查询\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'between\', [\'2020-5-20\', \'2022-7-21\'])\r\n    -&gt;select();\r\n// 不在某个时间区间\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'not between\', [\'2020-5-20\', \'2022-7-21\'])\r\n    -&gt;select();\r\n\r\n// 查询3个小时内的订单\r\nDb::name(\'order\')\r\n	-&gt;whereTime(\'createtime\',\'-3 hours\')\r\n    -&gt;select();\r\n\r\n// 获取今天的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'d\')\r\n    -&gt;select();\r\n    \r\n// 获取本周的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'w\')\r\n    -&gt;select();   \r\n    \r\n// 获取本月的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'m\')\r\n    -&gt;select();   \r\n    \r\n// 获取今年的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'y\')\r\n    -&gt;select();\r\n\r\n\r\n// 获取今天的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'today\')\r\n    -&gt;select();\r\n    \r\n// 获取昨天的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'yesterday\')\r\n    -&gt;select();\r\n    \r\n// 获取本周的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'week\')\r\n    -&gt;select();   \r\n    \r\n// 获取上周的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'last week\')\r\n    -&gt;select();    \r\n    \r\n// 获取本月的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'month\')\r\n    -&gt;select();   \r\n    \r\n// 获取上月的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'last month\')\r\n    -&gt;select();      \r\n    \r\n// 获取今年的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'year\')\r\n    -&gt;select();    \r\n    \r\n// 获取去年的订单\r\nDb::name(\'order\')\r\n    -&gt;whereTime(\'createtime\', \'last year\')\r\n    -&gt;select(); </code></pre>\r\n<br /><br /><p><br /></p>', 1, 1629167548, 1660888379, 1629167347, NULL, '', '', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (116, 3, 'Fastadmin 里面的导入表格简单介绍', NULL, '', '', NULL, '', 116, 409, '', '<p>引用的代码：</p><pre><code class=\"lang-php\">use Exception;\r\nuse PhpOffice\\PhpSpreadsheet\\Cell\\Coordinate;\r\nuse PhpOffice\\PhpSpreadsheet\\Reader\\Xlsx;\r\nuse PhpOffice\\PhpSpreadsheet\\Reader\\Xls;\r\nuse PhpOffice\\PhpSpreadsheet\\Reader\\Csv;<br></code></pre><p><br></p><p><br></p><p>admin里面继承方法 这是导入的文件</p><pre><code class=\"lang-php\">    /**\r\n     * 导入\r\n     */\r\n    public function import()\r\n    {\r\n        $file = $this-&gt;request-&gt;request(\'file\');\r\n        if (!$file) {\r\n            $this-&gt;error(__(\'Parameter %s can not be empty\', \'file\'));\r\n        }\r\n        $filePath = ROOT_PATH . DS . \'public\' . DS . $file;\r\n        if (!is_file($filePath)) {\r\n            $this-&gt;error(__(\'No results were found\'));\r\n        }\r\n        //实例化reader\r\n        $ext = pathinfo($filePath, PATHINFO_EXTENSION);\r\n        if (!in_array($ext, [\'csv\', \'xls\', \'xlsx\'])) {\r\n            $this-&gt;error(__(\'Unknown data format\'));\r\n        }\r\n        if ($ext === \'csv\') {\r\n            $file = fopen($filePath, \'r\');\r\n            $filePath = tempnam(sys_get_temp_dir(), \'import_csv\');\r\n            $fp = fopen($filePath, \"w\");\r\n            $n = 0;\r\n            while ($line = fgets($file)) {\r\n                $line = rtrim($line, \"\\n\\r\\0\");\r\n                $encoding = mb_detect_encoding($line, [\'utf-8\', \'gbk\', \'latin1\', \'big5\']);\r\n                if ($encoding != \'utf-8\') {\r\n                    $line = mb_convert_encoding($line, \'utf-8\', $encoding);\r\n                }\r\n                if ($n == 0 || preg_match(\'/^\".*\"$/\', $line)) {\r\n                    fwrite($fp, $line . \"\\n\");\r\n                } else {\r\n                    fwrite($fp, \'\"\' . str_replace([\'\"\', \',\'], [\'\"\"\', \'\",\"\'], $line) . \"\\\"\\n\");\r\n                }\r\n                $n++;\r\n            }\r\n            fclose($file) || fclose($fp);\r\n\r\n            $reader = new Csv();\r\n        } elseif ($ext === \'xls\') {\r\n            $reader = new Xls();\r\n        } else {\r\n            $reader = new Xlsx();\r\n        }<br></code></pre><p><br></p><p><br></p><p>开始常用的介绍</p><pre><code class=\"lang-php\"> $currentSheet = $PHPExcel-&gt;getSheet(0);  //读取文件中的第一个工作表\r\n           \r\n            $allColumn = $currentSheet-&gt;getHighestDataColumn(); //取得最大的列号Aj \r\n            // halt($allColumn);//Aj\r\n            // halt($currentSheet);\r\n            $allRow = $currentSheet-&gt;getHighestRow(); //取得一共有多少行 69\r\n            // halt($allRow);//69\r\n            $maxColumnNumber = Coordinate::columnIndexFromString($allColumn);//Aj \r\n            // halt($maxColumnNumber);//36\r\n            // halt();\r\n            $fields = [];\r\n            // for ($currentRow = 1; $currentRow &lt;= 1; $currentRow++) { //\r\n            //     for ($currentColumn = 1; $currentColumn &lt;= $maxColumnNumber; $currentColumn++) {\r\n            //         $val = $currentSheet-&gt;getCellByColumnAndRow($currentColumn, $currentRow)-&gt;getValue();\r\n        \r\n             \r\n            //         $fields[] = $val;\r\n            //     }\r\n            // }\r\n\r\n      \r\n                //                                          列，行\r\n            $diy_val = $currentSheet-&gt;getCellByColumnAndRow(36, 6)-&gt;getValue();//////获取某个单元的值\r\n            $diy_val = $currentSheet-&gt;getCellByColumnAndRow(1, 8)-&gt;getValue();////获取某个单元的值\r\n            halt($diy_val);<br></code></pre>', 1, 1629958304, 1653983929, 1629957912, NULL, '', '', '');
