path=\"/$appName/$streamName\";\r\n        $aliveUrl=\'rtmp://\'.self::ALIVE_URL.\"$path?\".self::createSign($path,$endTime,self::ALIVE_KEY);\r\n \r\n        //创建播流\r\n        $rtmp_sstring = \'/\'.$appName.\'/\'.$streamName.\'-\'.$endTime.\'-0-0-\'.self::TLIVE_KEY;\r\n        $rtmp_md5hash = md5($rtmp_sstring);\r\n        $play_url = \'://\'.self::TLIVE_URL.\'/\'.$appName.\'/\'.$streamName.\'?auth_key=\'.$endTime.\'-0-0-\'.$rtmp_md5hash;\r\n        $rtmp_play_url = \'rtmp\'.$play_url;\r\n        $artc_play_url = \'artc\'.$play_url;//超低延迟播放地址\r\n\r\n        $flv_sstring = \'/\'.$appName.\'/\'.$streamName.\'.flv-\'.$endTime.\'-0-0-\'.self::TLIVE_KEY;\r\n        $flv_md5hash = md5($flv_sstring);\r\n        $flv_play_url = \'//\'.self::TLIVE_URL.\'/\'.$appName.\'/\'.$streamName.\'.flv?auth_key=\'.$endTime.\'-0-0-\'.$flv_md5hash;//其他域名里播放请添加 HTTP头配置 Access-Control-Allow-Origin为*\r\n \r\n        $hls_sstring = \'/\'.$appName.\'/\'.$streamName.\'.m3u8-\'.$endTime.\'-0-0-\'.self::TLIVE_KEY;\r\n        $hls_md5hash = md5($hls_sstring);\r\n        $hls_play_url = \'//\'.self::TLIVE_URL.\'/\'.$appName.\'/\'.$streamName.\'.m3u8?auth_key=\'.$endTime.\'-0-0-\'.$hls_md5hash;//其他域名里播放请添加 HTTP头配置 Access-Control-Allow-Origin为*\r\n\r\n  \r\n\r\n        $data = [\r\n            \'rtmp\'=&gt;$rtmp_play_url,\r\n            \'flv\'=&gt;$flv_play_url,\r\n            \'hls\'=&gt;$hls_play_url,\r\n            \'artc\'=&gt;$artc_play_url,\r\n        ];\r\n        return [\r\n            \'alive_url\'=&gt;$aliveUrl,\r\n            \'tlive_url\'=&gt;$data,\r\n        ];\r\n\r\n\r\n    /**\r\n     * 创建签名\r\n     * @param $path 播放地址 域名后面所有\r\n     * @param $exp //结束时间\r\n     * @param $key\r\n     * @return string\r\n     */\r\n    private static function createSign($path,$exp,$key)\r\n    {\r\n        $rand=0;\r\n        $uid=0;\r\n        $str=sprintf(\"%s-%s-%s-%s-%s\",$path,(string)$exp,(string)$rand,(string)$uid,$key);\r\n        $hashValue=md5($str);\r\n        $authKey=sprintf(\"%s-%s-%s-%s\",(string)$exp,(string)$rand,(string)$uid,$hashValue);\r\n        return \"auth_key=$authKey\";\r\n    }\r\n\r\n    /**\r\n     * 停止直播\r\n     *\r\n     * @param string $appName app名\r\n     * @param string $streamName 房间ID\r\n     * @return void\r\n     */\r\n    public static function stopLive($appName,$streamName)\r\n    {\r\n        $query=[\r\n            \'RegionId\' =&gt; self::REGION_ID,\r\n            \'AppName\' =&gt; $appName,\r\n            \'StreamName\' =&gt; $streamName,\r\n            \'LiveStreamType\' =&gt; \"publisher\",\r\n            \'DomainName\' =&gt; self::DOMAIN_NAME,\r\n            // \'ResumeTime\'=&gt;\'\',\r\n        ];\r\n        $_this=new static();\r\n        $result=$_this-&gt;request(\'ForbidLiveStream\',$query);\r\n        return $result;\r\n    }\r\n\r\n    /**\r\n     * 恢复直播\r\n     *\r\n     * @param string $appName app名\r\n     * @param string $streamName 房间ID\r\n     * @return void\r\n     */\r\n    public static function resumeLive($appName,$streamName)\r\n    {\r\n        $query=[\r\n            \'RegionId\' =&gt; self::REGION_ID,\r\n            \'LiveStreamType\' =&gt; \"publisher\",\r\n            \'AppName\' =&gt; $appName,\r\n            \'StreamName\' =&gt; $streamName,\r\n            \'DomainName\' =&gt; self::DOMAIN_NAME,\r\n        ];\r\n        $_this=new static();\r\n        $result=$_this-&gt;request(\'ResumeLiveStream\',$query);\r\n        return $result;\r\n    }\r\n\r\n    \r\n    /**\r\n     * 获取直播在线人数\r\n     *\r\n     * @param string $appName app名\r\n     * @param string $streamName 房间ID\r\n     * @return void\r\n     */\r\n    public static function getOnlineUserNum($appName,$streamNma)\r\n    {\r\n        $query=[\r\n            \'RegionId\' =&gt; self::REGION_ID,\r\n            \'DomainName\' =&gt; self::DOMAIN_NAME,\r\n            \'AppName\'=&gt;$appName,\r\n            \'StreamName\' =&gt;$streamNma,\r\n        ];\r\n        $_this=new static();\r\n        $result=$_this-&gt;request(\'DescribeLiveStreamOnlineUserNum\',$query);\r\n        return $result;\r\n    }\r\n\r\n    //获取错误\r\n    public static function getError()\r\n    {\r\n        return (new static())-&gt;error;\r\n    }\r\n    \r\n    //请求\r\n    private function request($action,Array $query)\r\n    {\r\n        AlibabaCloud::accessKeyClient(self::ACCESS_KEY_ID, self::ACCESS_KEY_SECRET)\r\n            -&gt;regionId(self::REGION_ID)\r\n            -&gt;asDefaultClient();\r\n        try {\r\n            $result = AlibabaCloud::rpc()\r\n                -&gt;product(\'live\')\r\n                 -&gt;scheme(\'https\') // https | http\r\n                -&gt;version(\'2016-11-01\')\r\n                -&gt;action($action)\r\n                -&gt;method(\'POST\')\r\n                -&gt;host(self::LIVE_HOST)\r\n                -&gt;options([\r\n                    \'query\' =&gt; $query,\r\n                ])\r\n                -&gt;request();\r\n            return $result-&gt;toArray();\r\n        } catch (ClientException $e) {\r\n            $this-&gt;error=$e-&gt;getMessage();\r\n            return false;\r\n        } catch (ServerException $e) {\r\n            $this-&gt;error=$e-&gt;getMessage();\r\n            return false;\r\n        }\r\n    }\r\n\r\n    /**\r\n     * 回掉\r\n     *\r\n     * @return void\r\n     */\r\n    public function callback()\r\n    {   \r\n        $param = $this-&gt;request-&gt;param();\r\n        //日志 可以自行查看调试日志\r\n        file_put_contents($param[\'action\'].\'.txt\',date(\'Y-m-d-H-I-s\').json_encode($param).PHP_EOL,FILE_APPEND);\r\n\r\n        switch ($param[\'action\']) {\r\n            case \'publish\'://推送直播\r\n                //{\"action\":\"publish\",\"ip\":\"58.214.16.27\",\"id\":\"4\",\"app\":\"tlive.he4966.cn\",\"appname\":\"alive\",\"time\":\"1665390204\",\"usrargs\":\"vhost=alive.he4966.cn&amp;amp;auth_key=1665391198-0-0-10c534811ba5246ef078d5c616f70baf&amp;amp;ali_publisher_ip=58.214.16.27&amp;amp;ali_edge_node_ip=222.187.253.227&amp;amp;ali_node_via=cache27.cn5063%2clive11.l2et117&amp;amp;ali_node_ip=222.187.253.201#26%2c47.102.18.31#6&amp;amp;alilive_streamidv2=27.cn5063_40801_1246404453_1665390202202&amp;amp;alilive_clienthost=live11.l2et117\",\"node\":\"222.187.253.227\",\"width\":\"0\",\"height\":\"0\"}\r\n                $where[\'app_name\'] = $param[\'appname\'];\r\n                $where[\'id\'] = $param[\'id\'];\r\n                $update[\'ip\'] = $param[\'ip\'];\r\n                $update[\'live_status\'] = \'1\';//直播状态:1=直播中,2=未开始,3=已结束,4=禁播,5=暂停中,6=异常,7=已过期\r\n                Db::name(\'alive\')-&gt;where($where)-&gt;update($update);\r\n\r\n                break;\r\n\r\n            case \'publish_done\'://推送结束\r\n                //{\"action\":\"publish_done\",\"ip\":\"58.214.16.27\",\"id\":\"4\",\"app\":\"tlive.he4966.cn\",\"appname\":\"alive\",\"time\":\"1665390255\",\"usrargs\":\"vhost=alive.he4966.cn&amp;amp;auth_key=1665391198-0-0-10c534811ba5246ef078d5c616f70baf&amp;amp;ali_publisher_ip=58.214.16.27&amp;amp;ali_edge_node_ip=222.187.253.227&amp;amp;ali_node_via=cache27.cn5063%2clive11.l2et117&amp;amp;ali_node_ip=222.187.253.201#26%2c47.102.18.31#6&amp;amp;alilive_streamidv2=27.cn5063_40801_1246404453_1665390202202&amp;amp;alilive_clienthost=live11.l2et117\",\"node\":\"222.187.253.227\",\"width\":\"0\",\"height\":\"0\"}\r\n                $where[\'app_name\'] = $param[\'appname\'];\r\n                $where[\'id\'] = $param[\'id\'];\r\n                // $update[\'ip\'] = $param[\'ip\'];\r\n                $update[\'live_status\'] = \'3\';//直播状态:1=直播中,2=未开始,3=已结束,4=禁播,5=暂停中,6=异常,7=已过期\r\n                Db::name(\'alive\')-&gt;where($where)-&gt;update($update);\r\n\r\n                break;\r\n                \r\n            default:\r\n                \r\n                break;\r\n        }\r\n  \r\n    }\r\n\r\n    /**\r\n     * 获取直播列表\r\n     *\r\n     * @param string $live_status 直播状态:1=直播中,2=未开始,3=已结束,4=禁播,5=暂停中,6=异常,7=已过期\r\n     * @return void\r\n     */\r\n    public function get_live_list($live_status=null)\r\n    {\r\n        $where = null;\r\n        if($live_status)\r\n            $where[\'live_status\'] = $live_status;\r\n        $list = Db::name(\'alive\')-&gt;where($where)-&gt;select();\r\n        if($list){\r\n            foreach ($list as $key =&gt; &amp;$val) {\r\n                \r\n                $val[\'start_date\'] = date(\'Y-m-d H:i\',$val[\'start_time\']);\r\n                $val[\'end_date\'] = date(\'Y-m-d H:i\',$val[\'end_time\']);\r\n            }\r\n        }\r\n        $this-&gt;success(\'ok\',$list);\r\n    }\r\n\r\n\r\n    // /**\r\n    //  * 创建直播\r\n    //  *\r\n    //  * @param string $name 房间\r\n    //  * @param string $number 房间id\r\n    //  * @param string $end_date 结束时间\r\n    //  * @return void\r\n    //  */\r\n    // public function add_live($name=null,$number=null,$end_date=null)\r\n    // {\r\n    //     if($end_date){\r\n    //         $end_time = strtotime($end_date);\r\n    //     }else{\r\n    //         $this-&gt;error(\'亲传递结束时间\');\r\n    //     }\r\n        \r\n    //     $data = self::createdLive($name,$number,$end_time);\r\n    //     //\r\n    //     $this-&gt;success(\'ok\',$data);\r\n    // }\r\n \r\n}</code></pre>\r\n<br /></p><p><br /></p>\r\n<p><br /></p><p><br /></p><p>alive.sql 数据库</p><p><br /></p>\r\n<pre class=\"language-sql\"><code>CREATE TABLE `fa_alive` (\r\n  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT \'房间ID\',\r\n  `name` varchar(255) DEFAULT NULL COMMENT \'直播房间名\',\r\n  `app_name` varchar(255) NOT NULL DEFAULT \'alive\' COMMENT \'APP名\',\r\n  `start_time` int(10) NOT NULL COMMENT \'直播计划开始时间\',\r\n  `end_time` int(10) NOT NULL COMMENT \'直播计划结束时间\',\r\n  `description` varchar(255) DEFAULT NULL COMMENT \'直播简介\',\r\n  `image` varchar(255) DEFAULT NULL COMMENT \'直播封面图\',\r\n  `alive_url` varchar(255) DEFAULT NULL COMMENT \'推流地址\',\r\n  `tlive_json` varchar(2555) DEFAULT NULL COMMENT \'拉流JSON\',\r\n  `rtmp_url` varchar(255) DEFAULT NULL COMMENT \'rtmp拉流地址\',\r\n  `flv_url` varchar(255) DEFAULT NULL COMMENT \'flv拉流地址\',\r\n  `hls_url` varchar(255) DEFAULT NULL COMMENT \'hls拉流地址\',\r\n  `time` int(10) unsigned DEFAULT NULL COMMENT \'创建时间\',\r\n  `live_status` enum(\'1\',\'2\',\'3\',\'4\',\'5\',\'6\',\'7\') DEFAULT \'2\' COMMENT \'直播状态:1=直播中,2=未开始,3=已结束,4=禁播,5=暂停中,6=异常,7=已过期\',\r\n  `ip` varchar(20) DEFAULT NULL COMMENT \'直播IP\',\r\n  PRIMARY KEY (`id`) USING BTREE\r\n) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT=\'阿里直播\';</code></pre>\r\n<p><br /></p><p><span style=\"font-size:16px;\">前段控制器代码 访问地址传入参数id=</span></p><p><span style=\"font-size:16px;\"> </span></p>\r\n<pre class=\"language-php\"><code>    /**\r\n     * 查看直播\r\n     *\r\n     * @param int $id 房间ID\r\n     * @return void\r\n     */\r\n    public function live($id=null)\r\n    {\r\n        $data = Db::name(\'alive\')-&gt;where(\'id\',$id)-&gt;find();\r\n        if(!$data){\r\n            $this-&gt;error(\'没有该直播间\');\r\n        }\r\n        $title = $data[\'name\'];\r\n        //直播状态:1=直播中,2=未开始,3=已结束,4=禁播,5=暂停中,6=异常,7=已过期\r\n        $error = [\'1\'=&gt;\'直播中\',\'2\'=&gt;\'未开始\',\'3\'=&gt;\'已结束\',\'4\'=&gt;\'禁播\',\'5\'=&gt;\'暂停中\',\'6\'=&gt;\'异常\',\'7\'=&gt;\'已过期\'];\r\n        switch ($data[\'live_status\']) {\r\n            case \'1\':\r\n                \r\n                break;\r\n            \r\n            default:\r\n                // $this-&gt;error($error[$data[\'live_status\']]);\r\n                $title = $error[$data[\'live_status\']];\r\n                break;\r\n        }\r\n        $this-&gt;view-&gt;assign(\'title\', $title);//\r\n        $this-&gt;view-&gt;assign(\'data\', $data);//\r\n        return $this-&gt;view-&gt;fetch();\r\n    }</code></pre>\r\n<br /><br /><p><br /></p><p><br /></p><span style=\"color:#4D4D4D;font-family:-apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;font-size:16px;white-space:normal;background-color:#FFFFFF;\">网页播放代码</span><p><br /></p><p><br /></p>\r\n<pre class=\"language-html\"><code>&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n    &lt;meta charset=\"utf-8\"&gt;\r\n    &lt;meta http-equiv=\"x-ua-compatible\" content=\"IE=edge\" &gt;\r\n    &lt;meta name=\"viewport\" content=\"width=device-width, height=device-height, initial-scale=1, maximum-scale=1, minimum-scale=1, user-scalable=no\"/&gt;\r\n    &lt;title&gt;{$title}&lt;/title&gt;\r\n    &lt;link rel=\"stylesheet\" href=\"https://g.alicdn.com/de/prismplayer/2.9.16/skins/default/aliplayer-min.css\" /&gt;\r\n    &lt;script charset=\"utf-8\" type=\"text/javascript\" src=\"https://g.alicdn.com/de/prismplayer/2.9.16/aliplayer-min.js\"&gt;&lt;/script&gt;\r\n&lt;/head&gt;\r\n&lt;body&gt;\r\n&lt;div class=\"prism-player\" id=\"player-con\"&gt;&lt;/div&gt;\r\n&lt;script&gt;\r\n    var player = new Aliplayer({\r\n            \"id\": \"player-con\",\r\n        //M3U8格式使用\r\n            \"source\": \"{$data.hls_url}\",\r\n            \"width\": \"100%\",\r\n            \"height\": \"500px\",\r\n            \"autoplay\": true,\r\n        &nbsp;&nbsp;&nbsp;&nbsp;//直播参数必须为true\r\n            \"isLive\": true,\r\n            \"rePlay\": false,\r\n            \"playsinline\": true,\r\n            \"preload\": true,\r\n            {if $data.image}\"cover\":\'{$data.image}\',{/if}\r\n            \"controlBarVisibility\": \"hover\",\r\n            \"useH5Prism\": true\r\n            \r\n        }, function (player) {\r\n            console.log(\"The player is created\");\r\n        }\r\n    );\r\n&lt;/script&gt;\r\n&lt;/body&gt;</code></pre>\r\n<p><br /></p><p>超低延迟直播</p><p>\r\n<pre class=\"language-html\"><code>&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n&lt;head&gt;\r\n&lt;meta charset=\"utf-8\"&gt;\r\n&lt;meta http-equiv=\"x-ua-compatible\" content=\"IE=edge\" &gt;\r\n&lt;meta name=\"viewport\" content=\"width=device-width, height=device-height, initial-scale=1, maximum-scale=1, minimum-scale=1, user-scalable=no\"/&gt;\r\n&lt;title&gt;{$title}&lt;/title&gt;\r\n&lt;link rel=\"stylesheet\" href=\"https://g.alicdn.com/de/prismplayer/2.9.23/skins/default/aliplayer-min.css\" /&gt;\r\n&lt;script type=\"text/javascript\" charset=\"utf-8\" src=\"https://g.alicdn.com/de/prismplayer/2.9.23/aliplayer-min.js\"&gt;&lt;/script&gt;\r\n\r\n&lt;/head&gt;\r\n&lt;style&gt;\r\n	*{\r\n		margin: 0;\r\n		padding: 0;\r\n		list-style: none;\r\n	}\r\n	.prism-player{\r\n		width: 100%;\r\n		height: 100%;\r\n	}\r\n&lt;/style&gt;\r\n\r\n&lt;body&gt;\r\n  &lt;div class=\"prism-player\" id=\"player-con\"&gt;&lt;/div&gt;\r\n&lt;script src=\"https://cdn.bootcss.com/jquery/3.4.1/jquery.min.js\"&gt;&lt;/script&gt;\r\n&lt;script&gt;\r\n\r\n    var hh = $(window).height();\r\n	console.log(hh);\r\n	$(\'.prism-player\').css(\'height\',hh+\'px\');\r\n  /**\r\n   * 播放器默认播放 source 提供的 rts 拉流地址，如果失败，则会自动降级至 rtsFallbackSource 提供的拉流地址（如 HLS 地址）。\r\n   * 可能的降级场景包括：\r\n   * 1. 浏览器不支持 RTS，直接降级\r\n   * 2. RTS 信令请求失败（拉流地址无效、https配置无效、RTS配置无效等），直接降级\r\n   * 3. RTS 起播超时或中途断流，按自定义策略重试失败后降级\r\n   **/\r\n\r\n  // 更多播放器配置请参考 https://player.alicdn.com/aliplayer/index.html\r\n  var options = {\r\n    \"id\": \"player-con\",\r\n    \"source\": \"{$data.artc_url}\",\r\n    \"rtsFallbackSource\": \"https:{$data.hls_url}\",\r\n    // \"source\": \"RTS播放地址\",\r\n    // \"rtsFallbackSource\": \"降级地址，如HLS\",\r\n    \"width\": \"100%\",\r\n    \"height\": \"500px\",\r\n    {if $data.image}\"cover\":\'{$data.image}\',{/if}\r\n    \"autoplay\": true,\r\n    \"isLive\": true,\r\n    \"playsinline\": true,\r\n    \"useH5Prism\": true,\r\n    \"skipRtsSupportCheck\": false, // 对于不在 https://help.aliyun.com/document_detail/397569.html 中的浏览器，可以传 true 跳过检查，强制使用 RTS（有风险，需要自测保证）\r\n\r\n    /**\r\n     * RTS 拉流超时会默认重试\r\n     * 以下两个参数用来控制降级之前的重试策略，比如 3000 毫秒超时，重试一次，如果再拉不到流就降级，那么总共等待 6000 毫秒降级\r\n     **/\r\n    // RTS 多久拉不到流会重试，默认 3000 ms\r\n    // rtsLoadDataTimeout: 2000,\r\n\r\n    // RTS 拉不到流重试的次数，默认 5，此参数建议设为 1，即重试 1 次后降级，可以减少降级等待时间\r\n    liveRetry: 2,\r\n  };\r\n\r\n  var player = new Aliplayer(options, function () {\r\n    /* player ready */\r\n    });\r\n\r\n  // 降级时会触发此事件\r\n  player.on(\'rtsFallback\', function(event) {\r\n    console.log(\'[EVENT]rtsFallback\', event.paramData);\r\n    // event.paramData.reason 降级的原因\r\n    // event.paramData.fallbackUrl 降级到的地址\r\n  })\r\n\r\n  player.on(\'error\', function(event) {\r\n    console.log(\'[EVENT]error\', event.paramData);\r\n  })\r\n\r\n&lt;/script&gt;\r\n&lt;/body&gt;\r\n</code></pre>\r\n<br /></p><br /><br />', 1, 1664415144, 1665736661, 1664414500, NULL, '温馨提示微信小程序没有权限开通微信直播的可以用小程序嵌入web ,放个web即可观看直播哦', '', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (132, 2, 'thinkphp的队列使用 queue', NULL, '', '', NULL, '', 131, 513, '', '<p>安装命令(thinkphp一般自带) 可以自行查看 composer show</p><p><br /></p>\r\n<pre class=\"language-bash\"><code>composer require topthink/think-queue</code></pre>\r\n配置文件正常都在<p><br /></p><p><span style=\"color:#4D4D4D;font-family:-apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;font-size:16px;background-color:#FFFFFF;\">application\\extra\\queue.php</span></p><p><br /></p>\r\n<pre class=\"language-php\"><code>&lt;?php\r\n\r\nreturn [\r\n    // 用redis示例\r\n    \'connector\'=&gt;\'redis\',\r\n    \'expire\'     =&gt; 36000,\r\n    \'default\'    =&gt; \'Xiaohe\',\r\n    \'host\'       =&gt; \'127.0.0.1\',\r\n    \'port\'       =&gt; 6379,\r\n    \'password\'   =&gt; \'\',\r\n    \'select\'     =&gt; 6,\r\n    \'timeout\'    =&gt; 0,\r\n    \'persistent\' =&gt; false\r\n\r\n    //database\r\n    //    \'connector\' =&gt; \'database\',\r\n    //    \'expire\'  =&gt; 60,\r\n    //    \'default\' =&gt; \'default\',\r\n    //    \'table\'   =&gt; \'faqueue_jobs\',\r\n    //    \'dsn\'     =&gt; [\r\n    //        \'type\' =&gt; \'mysql\',\r\n    //        \'database\' =&gt; \'\',\r\n    //        \'hostname\' =&gt; \'\',\r\n    //        \'username\' =&gt; \'\',\r\n    //        \'password\' =&gt; \'\',\r\n    //        \'prefix\' =&gt; \'\',\r\n    //    ]\r\n\r\n];</code></pre>\r\n<p><br /></p><p><br /></p><p>加入任务</p><p><br /></p>\r\n<pre class=\"language-php\"><code>            $data = $this-&gt;request-&gt;param();\r\n</code><p><code>            //建议$data值放少一点,服务器内存大或者任务少随便放</code></p><p><code>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//<span style=\"font-family:Consolas, Monaco, &quot;Andale Mono&quot;, &quot;Ubuntu Mono&quot;, monospace;\">import_pct 是任务名称一会儿运行命令需要可以自己改</span> </code></p><code>            $res = \\think\\Queue::push(\'app\\api\\controller\\Task@AddTask\',$data , \'import_pct\');\r\n            </code>\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//延迟600秒执行该任务\r\n<p><code>            //$res = \\think\\Queue::later(600,\'app\\api\\controller\\Task@AddTask\',$data , \'import_pct\');</code></p><code>            $this-&gt;success(\'已加入后台导入任务\');</code>\r\n</pre>\r\n<br />处理任务<p><br /></p><p>application/api/controller/AddTask.php</p><p><br /></p>\r\n<p><br /></p><p><br /></p>\r\n<pre class=\"language-php\"><code>use think\\Queue;\r\nuse think\\Queue\\Job;\r\n\r\n    /**\r\n     * 添加的任务处理\r\n     *\r\n     * @return void\r\n     */\r\n    public function AddTask(Job $job,$data)\r\n    {\r\n        //这里面可以长时间执行但是超长时间运行 在执行运行命令加上超时时间,(毕竟有默认超时时间)\r\n        var_dump($data);\r\n        $data[\'name\'] = \'没有该表名\';\r\n        if(isset($this-&gt;arr[$data[\'table\']])){\r\n            $data[\'name\'] = $this-&gt;arr[$data[\'table\']];\r\n        }\r\n        $s = time();\r\n        $insert_id = $this-&gt;insert_task($data);\r\n        if(!$insert_id){//如果插入任务失败直接删除该任务\r\n            $job-&gt;delete();\r\n            return \'该任务被删除\';\r\n        }else{\r\n            $data[\'id\'] = $insert_id;\r\n            switch ($data[\'table\']) {\r\n                case \'pct_valid\':\r\n                    $Admin = new \\app\\api\\controller\\PctValid;\r\n                    break;\r\n                \r\n                default:\r\n                    # code...\r\n                    break;\r\n            }\r\n            $msg = $Admin-&gt;import($data);//开始执行导入  返回结果\r\n            if($msg==1){\r\n                Db::name(\'task\')-&gt;where(\'id\',$insert_id)-&gt;update([\'e_time\'=&gt;time(),\'s\'=&gt;(time()-$s),\'status\'=&gt;\'3\']);\r\n                $job-&gt;delete();\r\n                return \'导入成功\';\r\n            }else{\r\n                var_dump(\'任务ID\'.$insert_id .$msg);\r\n                Db::name(\'task\')-&gt;where(\'id\',$insert_id)-&gt;update([\'status\'=&gt;\'4\',\'error\'=&gt;$msg]);\r\n                $job-&gt;delete();\r\n                return \'导入失败\';\r\n            }\r\n\r\n        }\r\n}</code></pre>\r\n<p><br /></p><p><br /></p><p><br /></p><p><br /></p>\r\n<pre class=\"language-php\"><code>队列监听命令\r\n</code><p><code>`php think queue:work --queue import_pct`</code></p><p><code>\r\n</code></p><code>运行超时时间(秒)\r\n`php think queue:listen --queue import_pct --timeout=8000`</code></pre>\r\n<br /><p>详细文档参考</p><p><a href=\"https://github.com/coolseven/notes/blob/master/thinkphp-queue/README.md\" target=\"_blank\">https://github.com/coolseven/notes/blob/master/thinkphp-queue/README.md</a></p><br />', 1, 1668145053, 1716449519, 1668144362, NULL, 'he4966.cn', 'queue 队列', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (133, 3, '七牛云插件 获取token', NULL, '', '', NULL, '', 133, 459, '', '<p>Fastadmin插件 七牛云</p><p><br /></p><p>加入代码</p><p>\r\n<pre class=\"language-php\"><code>    /**\r\n     * 获取token\r\n     *\r\n     * @return void\r\n     */\r\n    public function get_token($slef=false)\r\n    {\r\n     \r\n        Config::set(\'default_return_type\', \'json\');\r\n\r\n        // $this-&gt;check();\r\n        $config = get_addon_config(\'qiniu\');\r\n      \r\n \r\n        $config[\'savekey\'] = str_replace([\'{year}\', \'{mon}\', \'{day}\', \'{filemd5}\', \'{.suffix}\'], [\'$(year)\', \'$(mon)\', \'$(day)\', \'$(etag)\', \'$(ext)\'], $config[\'savekey\']);\r\n\r\n        // 构建鉴权对象\r\n        $auth = new Auth($config[\'accessKey\'], $config[\'secretKey\']);\r\n        // 生成上传 Token\r\n        $token = $auth-&gt;uploadToken($config[\'bucket\'], null, 3600, [\'saveKey\' =&gt; ltrim($config[\'savekey\'], \'/\')]);\r\n        \r\n        if($slef)\r\n            return $token;\r\n\r\n        $this-&gt;success(\"token\", \'\', [\'token\'=&gt;$token]);\r\n    }</code></pre>\r\n访问地址:域名+/addons/qiniu/index/get_token&nbsp;&nbsp;</p><p>注意token泄露<br /></p>', 1, 1669082940, 1669082940, 1669082703, NULL, '', '', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (134, 3, '支付插件epay 创建订单', NULL, '', '', NULL, '', 134, 529, '', '<p><a class=\"title\" href=\"https://www.fastadmin.net/store/epay.html\" data-toggle=\"tooltip\" title=\"\" target=\"_blank\" data-original-title=\"查看插件介绍和帮助\" aria-describedby=\"tooltip653437\" style=\"box-sizing:border-box;background-color:#F5F5F5;color:#444444;text-decoration-line:none;outline:none;font-family:&quot;Source Sans Pro&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;font-size:14px;white-space:normal;\">微信支付宝整合插件</a>版本是1.2.9</p><p>微信公众号支付示例：</p><p><br /></p>\r\n<pre class=\"language-php\"><code>\r\n    /**\r\n     * 返回微信公众号支付信息\r\n     *\r\n     * @return void\r\n     */\r\n    public function order($order = null)\r\n    {\r\n        // halt($order);\r\n        $user = Session::get(\'wechat_user\');\r\n        if (!$user) {\r\n            $this-&gt;error(\'请登陆\', url(\'/cms/user/login\'));\r\n            // halt($user);\r\n        }\r\n\r\n        // $app = $this-&gt;get_wx_gzh_app();\r\n        $app = (new \\app\\api\\controller\\Tpmecms)-&gt;get_wx_gzh_app();\r\n        $order_data = Db::name(\'goodsorder\')-&gt;where(\'order\', $order)-&gt;find();\r\n        // halt($order_data);\r\n        $user = Db::name(\'user\')-&gt;where(\'id\', $order_data[\'uid\'])-&gt;find();\r\n        // $result = $app-&gt;order-&gt;unify([\r\n        //     \'body\' =&gt; \'购买商品\',\r\n        //     \'out_trade_no\' =&gt; \'123234720231\',\r\n        //     \'total_fee\' =&gt; 1,\r\n        //     // \'spbill_create_ip\' =&gt; \'123.12.12.123\', // 可选，如不传该参数，SDK 将会自动获取相应 IP 地址\r\n        //     \'notify_url\' =&gt; $this-&gt;request-&gt;domain().\'pay/notify?order=\'.$order, // 支付结果通知网址，如果不设置则会使用配置里的默认地址\r\n        //     \'trade_type\' =&gt; \'JSAPI\', // 请对应换成你的支付方式对应的值类型\r\n        //     \'openid\' =&gt; $user[\'wx_openid\'],\r\n        // ]);\r\n\r\n        // 商品信息\r\n        $goods = Db::name(\'goods\')-&gt;where(\'id\', $order_data[\'goods_id\'])-&gt;find();\r\n\r\n        $params = [\r\n            \'amount\' =&gt; \\think\\Config::get(\'site.pay_test\')?\"0.01\":$order_data[\'money\'], //,, //$order_data[\'money\'],\r\n            \'orderid\' =&gt; $order,\r\n            \'type\' =&gt; \"wechat\",\r\n            \'title\' =&gt; $goods[\'name\'],\r\n            \'notifyurl\' =&gt; $this-&gt;request-&gt;domain() . \'/api/pay/query_goods_order?order=\' . $order, //支付回掉\r\n            \'returnurl\' =&gt; $this-&gt;request-&gt;domain() . \'/cms/order/order_info?order=\' . $order,\r\n            \'method\' =&gt; \"mp\",\r\n            \'openid\' =&gt; $user[\'wx_openid\'],\r\n            // \'auth_code\'=&gt;\"验证码\"\r\n        ];\r\n\r\n         $payData = \\addons\\epay\\library\\Service::submitOrder($params);\r\n\r\n        $app-&gt;jssdk-&gt;setUrl($this-&gt;request-&gt;domain() . \'/cms/pay/\');\r\n\r\n        $this-&gt;view-&gt;assign(\"orderData\", $params);\r\n        $this-&gt;view-&gt;assign(\"payData\", json_decode($payData, true));\r\n\r\n\r\n        // halt($thi-&gt;request-&gt;param());\r\n        return $this-&gt;view-&gt;fetch();\r\n        // return $this-&gt;view-&gt;fetch(\'/shop/goods\');\r\n    }</code></pre>\r\n<p><br /></p><br /><p></p><p>js部分</p><p>\r\n<pre class=\"language-javascript\"><p><code><div style=\"color:#D4D4D4;background-color:#1E1E1E;font-family:Menlo, Monaco, &quot;Courier New&quot;, monospace;font-size:12px;line-height:18px;white-space:pre;\"> {include file=\"shop/com/wx_config\" /}</div>        </code></p><p><code>function pay(order){</code></p><code>            $.post(\"/cms/pay/pay\", { order: order }, function (data, status) {\r\n                console.log(\'支付信息\',data);\r\n                wx.chooseWXPay({\r\n                        // appId:data.appId,\r\n                        timestamp: data.timeStamp, // 支付签名时间戳，注意微信 jssdk 中的所有使用 timestamp 字段均为小写。但最新版的支付后台生成签名使用的 timeStamp 字段名需大写其中的 S 字符\r\n                        nonceStr: data.nonceStr, // 支付签名随机串，不长于 32 位\r\n                        package: data.package, // 统一支付接口返回的prepay_id参数值，提交格式如：prepay_id=\\*\\*\\*）\r\n                        // package: \'prepay_id=\'+data.prepay_id, // 统一支付接口返回的prepay_id参数值，提交格式如：prepay_id=\\*\\*\\*）\r\n                        \r\n                        signType: data.signType, // 微信支付V3的传入 RSA ,微信支付V2的传入格式与V2统一下单的签名格式保持一致\r\n                        paySign: data.paySign, // 支付签名\r\n                        success: function (res) {\r\n                            // 支付成功后的回调函数\r\n                            alert(\'支付成功\');\r\n                            window.location.href = \'/cms/shop/order_info?order=\'+order;\r\n                        }\r\n                        });\r\n\r\n            });\r\n        }</code></pre>\r\n<br /></p>', 1, 1670669387, 1715909243, 1670669224, NULL, '', '我最好了', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (135, 2, 'easywechat 微信公众号支付', NULL, '', '', NULL, '', 135, 618, '', '<p>\r\n<pre class=\"language-php\"><code> //记得绑定支付目录到该控制器下面 \r\n\r\n/**\r\n     * 支付信息5.x\r\n     *\r\n     * @param string $order\r\n     * @return void\r\n     */\r\n    public function pay($order = null)\r\n    {\r\n        $pay_app = (new \\app\\api\\controller\\Tpmecms)-&gt;get_pay_app();\r\n        $order_data = Db::name(\'goodsorder\')-&gt;where(\'order\', $order)-&gt;find();\r\n\r\n        // 商品信息\r\n        $goods = Db::name(\'goods\')-&gt;where(\'id\', $order_data[\'goods_id\'])-&gt;find();\r\n\r\n        $user = Db::name(\'user\')-&gt;where(\'id\', $order_data[\'uid\'])-&gt;find();\r\n        $payData = $pay_app-&gt;order-&gt;unify([\r\n            \'body\' =&gt; $goods[\'name\'],\r\n            \'out_trade_no\' =&gt; $order,\r\n            \'total_fee\' =&gt; (\\think\\Config::get(\'site.pay_test\') ? 0.02 : $order_data[\'money\']) * 100,\r\n            // \'spbill_create_ip\' =&gt; \'123.12.12.123\', // 可选，如不传该参数，SDK 将会自动获取相应 IP 地址\r\n            \'notify_url\' =&gt; $this-&gt;request-&gt;domain() . \'/api/pay/query_goods_order?order=\' . $order, // 支付结果通知网址，如果不设置则会使用配置里的默认地址\r\n            \'trade_type\' =&gt; \'JSAPI\', // 请对应换成你的支付方式对应的值类型\r\n            \'openid\' =&gt; $user[\'wx_openid\']\r\n        ]);\r\n\r\n\r\n        $jssdk = $pay_app-&gt;jssdk;\r\n        $config = $jssdk-&gt;bridgeConfig($payData[\'prepay_id\'], false); // 返回 json 字符串，如果想返回数组，传第二个参数 false\r\n        // $config = $jssdk-&gt;sdkConfig($payData[\'prepay_id\']);\r\n        // halt($config);\r\n\r\n        $res = \\think\\Queue::later(30, \'app\\api\\controller\\Task@QueryOrder\', $order, \'task\');\r\n\r\n        header(\'Content-Type:application/json; charset=utf-8\');\r\n        exit(json_encode($config));\r\n    }</code></pre>\r\n<br /></p>', 1, 1670679558, 1670679558, 1670679444, NULL, '', '', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (136, 2, 'thinkphp上传图片加文字水印 满屏', NULL, '', '', NULL, '', 136, 516, '', '<p>在代码<span style=\"white-space:normal;\">app/</span><span style=\"white-space:normal;\">api/</span><span style=\"white-space:normal;\">behavior/</span>tags.php里面添加</p><p><br /></p>\r\n<pre class=\"language-php\"><code>\r\n    \'upload_after\' =&gt; [\r\n        \'app\\\\api\\\\behavior\\\\UploadAfter\',//可填写自己命名的文件\r\n    ],\r\n    </code></pre>\r\n<br />完整代码tags.php<p><br /></p><p><br /></p>\r\n<pre class=\"language-php\"><code>return [\r\n\r\n    \'upload_after\' =&gt; [\r\n        \'app\\\\api\\\\behavior\\\\UploadAfter\',\r\n    ],\r\n    \r\n];</code></pre>\r\n<p><br /></p><p><br /></p><p>在代码创建代码在目录模块 app/api/behavior/</p><p><br /></p><p>文件名UploadAfter.php (要和tags里面的对应哦)</p><p><br /></p>\r\n<pre class=\"language-php\"><code>&lt;?php\r\n\r\nnamespace app\\api\\behavior;\r\n\r\nuse think\\image\\Exception as ImageException;\r\n\r\nclass UploadAfter\r\n{\r\n    public function run(&amp;$params)\r\n    {\r\n        if (request()-&gt;action() == \'upload_water_image\' || request()-&gt;post(\'type\') == \'image_water\') { //更具自己的方法或者参数来判断是否要加入水印 这里方法和post参数type 为里面的值即可QQ496631085 he4966.cn\r\n            // $new_file_name = $uploadDir . $file-&gt;getSaveName();\r\n            if (in_array($params[\'mimetype\'], [\'image/gif\', \'image/jpg\', \'image/jpeg\', \'image/bmp\', \'image/png\', \'image/webp\']) || in_array($suffix, [\'gif\', \'jpg\', \'jpeg\', \'bmp\', \'png\', \'webp\'])) {\r\n\r\n                $image = \\think\\Image::open(\'.\' . $params[\'url\']);\r\n                $scene_type = request()-&gt;post(\'type\', \'image_water\');\r\n                $font_path = \\think\\Config::get(\'site.water_text\'); //水印字体路径\r\n                $font_color = \\think\\Config::get(\'site.water_color\') ?? \'#3c3c3c\'; //水印文字颜色\r\n\r\n                switch ($scene_type) {\r\n                    case \'product_thumb\':\r\n                        $splInfo = $image-&gt;thumb(200, 200)-&gt;save(\'.\' .  $params[\'url\']);\r\n                        break;\r\n                    case \'product_img\':\r\n                        $splInfo = $image-&gt;thumb(750, 750)-&gt;save(\'.\' .  $params[\'url\']);\r\n                        break;\r\n                    case \'product_detail_img\':\r\n                        $splInfo = $image-&gt;save(\'.\' .  $params[\'url\']);\r\n                        break;\r\n                    default:\r\n                        $size = $this-&gt;get_text_size($font_path, \'.\' . \\think\\Config::get(\'site.water_font\'), 30);\r\n                        $w = $size[\'w\'];\r\n                        $h = $size[\'h\'];\r\n                        for ($w_i = 0; $w_i &lt;= $image-&gt;width() / $w; $w_i++) {\r\n                            for ($h_i = 0; $h_i &lt;= $image-&gt;height() / $h; $h_i++) {\r\n                                //循环加入文字水印\r\n                                $image-&gt;text($font_path, \'.\' . \\think\\Config::get(\'site.water_font\'), 20, $font_color, [$w_i * ($w + 20), $h_i * ($h + 20)], 0, 15);\r\n                            }\r\n                        }\r\n                        $splInfo = $image-&gt;save(\'.\' .  $params[\'url\']);\r\n\r\n                        break;\r\n                }\r\n            } else {\r\n                // $splInfo = $file-&gt;validate([\'size\' =&gt; $size])-&gt;move(ROOT_PATH . \'/public\' . $uploadDir, $fileName);\r\n            }\r\n        }\r\n    }\r\n\r\n\r\n\r\n\r\n    /**\r\n     * 图像添加文字\r\n     *\r\n     * @param  string  $text   添加的文字\r\n     * @param  string  $font   字体路径\r\n     * @param  integer $size   字号\r\n     *\r\n     * @return $this\r\n     * @throws ImageException\r\n     */\r\n    public function get_text_size($text, $font, $size)\r\n    {\r\n\r\n        if (!is_file($font)) {\r\n            throw new ImageException(\"不存在的字体文件：{$font}\");\r\n        }\r\n        //获取文字信息\r\n        $info = imagettfbbox($size, 0, $font, $text);\r\n        $minx = min($info[0], $info[2], $info[4], $info[6]);\r\n        $maxx = max($info[0], $info[2], $info[4], $info[6]);\r\n        $miny = min($info[1], $info[3], $info[5], $info[7]);\r\n        $maxy = max($info[1], $info[3], $info[5], $info[7]);\r\n        /* 计算文字初始坐标和尺寸 */\r\n        $x = $minx;\r\n        $y = abs($miny);\r\n        $w = $maxx - $minx;\r\n        $h = $maxy - $miny;\r\n        return [\'w\' =&gt; $w, \'h\' =&gt; $h];\r\n    }\r\n}</code></pre>\r\n<p><br /></p><p>然后就可以写个接口方法名为\r\n</p>\r\n<pre class=\"language-php\" style=\"word-spacing:0px;\"><code>upload_water_image</code></pre>\r\n<p><br /></p><br /><p>示例/api/com/upload_water_image&nbsp;</p><p><br /></p>', 1, 1679451943, 1679451943, 1679451461, NULL, 'thinkphp 文字水印 满屏水印 fastadmin', 'thinkphp,文字水印,满屏水印,fastadmin', '');
INSERT INTO `fa_news` (`id`, `cate_id`, `title`, `hotdata`, `image`, `images`, `abstract`, `tags`, `weigh`, `views`, `memo`, `content`, `switch`, `createtime`, `updatetime`, `publishtime`, `deletetime`, `seotitle`, `keywords`, `description`) VALUES (138, 3, '后台统计 集成柱状图 带时间区间搜索layui+ echarts', NULL, '', '', NULL, 'echarts', 138, 369, '', '<p>如图</p><p><img src=\"https://tpmecms.he4966.cn/uploads/20230831/c86f4a5b4d3c36691aadd204c6b3eb83.png\" alt=\"\" /></p><p>html代码</p><p><br /></p>\r\n<pre class=\"language-html\"><code>&lt;!DOCTYPE html&gt;\r\n&lt;html&gt;\r\n  &lt;head&gt;\r\n    &lt;meta charset=\"utf-8\" /&gt;\r\n    &lt;title&gt;he4966&lt;/title&gt;\r\n    &lt;!-- ECharts 文件 --&gt;\r\n    &lt;script src=\"/assets/js/echarts.min2.js\"&gt;&lt;/script&gt;\r\n    &lt;script src=\"/assets/js/jq.js\" charset=\"UTF-8\"&gt;&lt;/script&gt;\r\n    &lt;script src=\"/layui/layui.js\"&gt;&lt;/script&gt;\r\n    &lt;link rel=\"stylesheet\" href=\"/layui/css/layui.css\"&gt;\r\n\r\n\r\n\r\n  &lt;/head&gt;\r\n  &lt;body&gt;\r\n    &lt;!-- 为 ECharts 准备一个定义了宽高的 DOM --&gt;                                      \r\n\r\n    &lt;!-- 青色分割线 --&gt;\r\n    &lt;hr class=\"layui-border-cyan\" style=\"opacity: 0.5;\"&gt;\r\n  &lt;form action=\"\" id=\"myForm\" method=\"post\"&gt;\r\n\r\n      &lt;div class=\"layui-form\"&gt;\r\n        &lt;div class=\"layui-inline\"&gt;\r\n          &lt;label class=\"layui-form-label\"&gt;开始日期&lt;/label&gt;\r\n          &lt;div class=\"layui-inline\" id=\"date-range\" &gt;\r\n            &lt;div style=\"display: flex;\"&gt;\r\n            \r\n              &lt;input type=\"text\" id=\"startDate\" name=\"startDate\" class=\"layui-input\" required lay-verify=\"required\"  placeholder=\"开始日期\" value=\"{$startDate}\" style=\"width: 120px;\"&gt;\r\n              &lt;label class=\"layui-form-label\"&gt;结束日期&lt;/label&gt;\r\n              &lt;input type=\"text\" id=\"endDate\" name=\"endDate\" class=\"layui-input\" required lay-verify=\"required\" placeholder=\"结束日期\" value=\"{$endDate}\" style=\"width: 120px;\"&gt;\r\n\r\n              &lt;button type=\"submit\" class=\"layui-btn layui-btn-primary layui-border-green\"&gt;查询&lt;/button&gt;\r\n            &lt;/div&gt;\r\n           \r\n          &lt;/div&gt;\r\n        &lt;/div&gt;\r\n      &lt;/div&gt;\r\n    &lt;/form&gt; \r\n\r\n    &lt;!-- 蓝色分割线 --&gt;\r\n    &lt;hr class=\"layui-border-blue\"&gt;\r\n\r\n    &lt;div id=\"main\" style=\"width: auto;height:450px;\"&gt;&lt;/div&gt;\r\n  &lt;script type=\"text/javascript\"&gt;\r\n\r\n\r\n\r\n      // 基于准备好的dom，初始化echarts实例\r\n      var myChart = echarts.init(document.getElementById(\'main\'));\r\n\r\n      // 指定图表的配置项和数据\r\n      var option = {\r\n        title: {\r\n          text: \'用户统计\'\r\n        },\r\n        tooltip: {},\r\n        legend: {\r\n          data: [\'数量\']\r\n        },\r\n        xAxis: {\r\n          data: [ {volist name=\"list\" id=\"v\"}\r\n              { value: \"{$key}\", name: \'{$key}\' },\r\n              {/volist}\r\n            ]\r\n        },\r\n        yAxis: {},\r\n\r\n\r\n        series: [\r\n          {\r\n            name: \'统计\',\r\n\r\n            type: \'bar\',//柱状\r\n            label: {\r\n              show: true,\r\n              rotate: 10,//旋转\r\n              position: \'top\',//在上面显示\r\n              distance: 15,// 距离图形元素的距离\r\n\r\n            },\r\n            itemStyle: {\r\n              color: function(params) {\r\n                // 随机生成颜色\r\n                return \'rgb(\' + [\r\n                  Math.round(Math.random() * 255),\r\n                  Math.round(Math.random() * 255),\r\n                  Math.round(Math.random() * 255)\r\n                ].join(\',\') + \')\';\r\n              }\r\n            },\r\n            fontSize: 12,\r\n            data: [ {volist name=\"list\" id=\"v\"}\r\n              { value: {$v}, name: \'{$key}\' },\r\n              {/volist}]\r\n          }\r\n        ],\r\n      };\r\n\r\n      // 使用刚指定的配置项和数据显示图表。\r\n      myChart.setOption(option);\r\n  &lt;/script&gt;\r\n\r\n\r\n\r\n \r\n\r\n\r\n  &lt;script&gt;\r\n    layui.use(function(){\r\n      var layer = layui.layer\r\n      ,form = layui.form\r\n      ,laypage = layui.laypage\r\n      ,element = layui.element\r\n      ,laydate = layui.laydate\r\n      ,util = layui.util;\r\n      \r\n      // //欢迎信息\r\n      // layer.msg(\'QQ496631085\');\r\n      \r\n      // //输出版本号\r\n      // lay(\'#version\').html(layui.v);\r\n        //时间选择器\r\n      laydate.render({ \r\n        elem: \'#date-range\' //开始时间和结束时间所在 input 框的父选择器\r\n        //设置开始日期、日期日期的 input 选择器\r\n        ,range: [\'#startDate\', \'#endDate\'] //数组格式为 layui 2.6.6 开始新增\r\n        ,format: \'yyyy-MM-dd\' //可任意组合\r\n        ,done: function(value, date, endDate){\r\n\r\n          //点击日期确定后自动提交表单\r\n          $(\'#myForm\').submit();\r\n    \r\n          console.log(value); //得到日期生成的值，如：2023-08-31\r\n          console.log(date); //得到日期时间对象：{year: 2023, month: 8, date: 31, hours: 0, minutes: 0, seconds: 0}\r\n          console.log(endDate); //得结束的日期时间对象，开启范围选择（range: true）才会返回。对象成员同上。\r\n        }\r\n      });\r\n\r\n    });\r\n  &lt;/script&gt;\r\n\r\n  &lt;/body&gt;\r\n&lt;/html&gt;\r\n\r\n\r\n</code></pre>\r\n<p><br /></p>php代码<p><br /></p><p><br /></p>\r\n<pre class=\"language-php\"><code>    protected $layout = null;\r\n\r\n    public function _initialize()\r\n    {\r\n        if($this-&gt;request-&gt;action()==\'index\'){\r\n            $this-&gt;layout = \'default\';\r\n        }\r\n        parent::_initialize();        \r\n\r\n    }\r\n\r\n\r\n    public function users($startDate=null,$endDate=null){\r\n  \r\n        if(!$startDate){\r\n            $startDate = date(\'Y-m-d\',strtotime(\'-7 day\'));\r\n        }\r\n        if(!$endDate){\r\n            $endDate = date(\'Y-m-d\');\r\n        }\r\n        $startTime = strtotime($startDate);\r\n        $endTime = strtotime($endDate);\r\n\r\n        $this-&gt;view-&gt;assign(\'startDate\',$startDate);\r\n        $this-&gt;view-&gt;assign(\'endDate\',$endDate);\r\n\r\n\r\n        $user_count = Db::name(\'user\')-&gt;count();\r\n\r\n\r\n        $list[\'总用户数\'] =  $user_count;\r\n        $list[\'新用户\'] =  Db::name(\'user\')\r\n            -&gt;where(\'jointime\',\'&gt;=\',$startTime)\r\n            -&gt;where(\'jointime\',\'&lt;=\',$endTime)\r\n            -&gt;count();\r\n\r\n        $list[\'充值用户\'] =  Db::name(\'money_order\')\r\n            -&gt;where(\'pay_time\',\'&gt;=\',$startTime)\r\n            -&gt;where(\'pay_time\',\'&lt;=\',$endTime)\r\n            -&gt;count();\r\n        $list[\'未充值用户\'] =  $user_count - $list[\'充值用户\'];\r\n\r\n            \r\n\r\n        $this-&gt;view-&gt;assign(\'list\',$list);\r\n\r\n\r\n        return $this-&gt;view-&gt;fetch();\r\n    }\r\n</code></pre>\r\n<br /><p><br /></p>', 1, 1693459373, 1693459560, 1693459219, NULL, 'echarts', 'echarts', 'echarts');
