INSERT INTO `fa_page` (`id`, `cate_id`, `title`, `content`, `description`, `weigh`, `views`, `createtime`, `updatetime`, `deletetime`) VALUES (2, 1, '简单介绍', '<div style=\"line-height: 1.4;\"><div style=\"line-height: 1.4;\"><span style=\"font-size: 18px;\">TpMeCMS是一款基于FastAdmin框架开发的，FastAdmin基于ThinkPHP5+Bootstrap开发的框架,核心代码简洁、明了，适用于微信小程序/公众号，PC网站，手机网站，App（Api接口）等集于一体的Tp框架，后台直接生成数据库的增删改查，方便二次开开发，了解thinkphp5即可上手</span></div><br><br><div style=\"line-height: 1.4;\"><span style=\"font-size: 18px;\">## TpMeCMS由来</span></div><div style=\"line-height: 1.4;\"><span style=\"font-size: 18px;\">    用过FastAdmin的插件cms，SIYUCMS，行云海CMS等，功能功能各有优点缺点，发现自己的项目经常使用（微信小程序/公众号，PC网站，手机网站，App（Api接口）等），结合还需要二次开发，才能使用，于是就使用自己熟悉等thinkphp5，FastAdmin来开发自己的cms框架</span></div><br><div style=\"line-height: 1.4;\"><span style=\"font-size: 18px;\">## TpMeCMS主要优点</span></div><div style=\"line-height: 1.4;\"><span style=\"font-size: 18px;\">    集成微信小程序和微信公众号功能，后台集成栏目管理等，接口可以直接拷贝app\\cms\\controller\\Cms</span></div></div>\r\n', '我最好的东西给你', 11, 0, 1580966471, 1715909133, NULL);
